import React, { useState, useEffect, useRef } from 'react';
import { Search, Globe, Check, ChevronDown } from 'lucide-react';
import GlassCard from '../UI/GlassCard';
import AnimatedButton from '../UI/AnimatedButton';

const CountrySelector = ({ 
  onCountrySelect, 
  selectedCountry = null, 
  placeholder = "Select your country",
  className = "" 
}) => {
  const [countries, setCountries] = useState([]);
  const [filteredCountries, setFilteredCountries] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    fetchCountries();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  useEffect(() => {
    console.log('Search term changed:', searchTerm);
    console.log('Available countries:', countries.length);
    
    if (searchTerm.trim() === '') {
      const initialCountries = countries.slice(0, 20);
      console.log('Setting initial countries:', initialCountries.length);
      setFilteredCountries(initialCountries);
    } else {
      const filtered = countries.filter(country =>
        country.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        country.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
        country.currency.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
        country.currency.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      console.log('Filtered countries:', filtered.length);
      setFilteredCountries(filtered.slice(0, 50));
    }
  }, [searchTerm, countries]);

  const fetchCountries = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/countries`);
      const data = await response.json();
      if (data.success) {
        setCountries(data.countries);
        setFilteredCountries(data.countries.slice(0, 20));
      } else {
        // Fallback to hardcoded countries if API fails
        console.warn('API returned error, using fallback data');
        useFallbackCountries();
      }
    } catch (error) {
      console.error('Error fetching countries:', error);
      // Use fallback data on error
      useFallbackCountries();
    } finally {
      setLoading(false);
    }
  };

  const useFallbackCountries = () => {
    const fallbackCountries = [
      { name: 'United States', code: 'US', currency: { code: 'USD', symbol: '$', name: 'US Dollar' } },
      { name: 'India', code: 'IN', currency: { code: 'INR', symbol: '‚Çπ', name: 'Indian Rupee' } },
      { name: 'United Kingdom', code: 'GB', currency: { code: 'GBP', symbol: '¬£', name: 'British Pound' } },
      { name: 'Canada', code: 'CA', currency: { code: 'CAD', symbol: 'C$', name: 'Canadian Dollar' } },
      { name: 'Australia', code: 'AU', currency: { code: 'AUD', symbol: 'A$', name: 'Australian Dollar' } },
      { name: 'Germany', code: 'DE', currency: { code: 'EUR', symbol: '‚Ç¨', name: 'Euro' } },
      { name: 'France', code: 'FR', currency: { code: 'EUR', symbol: '‚Ç¨', name: 'Euro' } },
      { name: 'Japan', code: 'JP', currency: { code: 'JPY', symbol: '¬•', name: 'Japanese Yen' } },
      { name: 'South Korea', code: 'KR', currency: { code: 'KRW', symbol: '‚Ç©', name: 'South Korean Won' } },
      { name: 'China', code: 'CN', currency: { code: 'CNY', symbol: '¬•', name: 'Chinese Yuan' } },
      { name: 'Brazil', code: 'BR', currency: { code: 'BRL', symbol: 'R$', name: 'Brazilian Real' } },
      { name: 'Mexico', code: 'MX', currency: { code: 'MXN', symbol: '$', name: 'Mexican Peso' } },
      { name: 'Nigeria', code: 'NG', currency: { code: 'NGN', symbol: '‚Ç¶', name: 'Nigerian Naira' } },
      { name: 'South Africa', code: 'ZA', currency: { code: 'ZAR', symbol: 'R', name: 'South African Rand' } },
      { name: 'Singapore', code: 'SG', currency: { code: 'SGD', symbol: 'S$', name: 'Singapore Dollar' } },
      { name: 'Thailand', code: 'TH', currency: { code: 'THB', symbol: '‡∏ø', name: 'Thai Baht' } },
      { name: 'Malaysia', code: 'MY', currency: { code: 'MYR', symbol: 'RM', name: 'Malaysian Ringgit' } },
      { name: 'Indonesia', code: 'ID', currency: { code: 'IDR', symbol: 'Rp', name: 'Indonesian Rupiah' } },
      { name: 'Philippines', code: 'PH', currency: { code: 'PHP', symbol: '‚Ç±', name: 'Philippine Peso' } },
      { name: 'Vietnam', code: 'VN', currency: { code: 'VND', symbol: '‚Ç´', name: 'Vietnamese Dong' } }
    ];
    setCountries(fallbackCountries);
    setFilteredCountries(fallbackCountries);
  };

  const handleCountrySelect = (country) => {
    onCountrySelect(country);
    setIsOpen(false);
    setSearchTerm('');
  };

  const handleToggle = () => {
    setIsOpen(!isOpen);
    if (!isOpen) {
      setSearchTerm('');
    }
  };

  return (
    <div className={`relative ${className}`} ref={dropdownRef}>
      <GlassCard className="bg-gradient-to-r from-white to-gray-50">
        <button
          onClick={handleToggle}
          className="w-full flex items-center justify-between p-4 text-left hover:bg-gray-50 transition-colors rounded-lg"
        >
          <div className="flex items-center">
            <Globe className="w-5 h-5 text-blue-600 mr-3" />
            <div>
              {selectedCountry ? (
                <div>
                  <p className="font-medium text-gray-900">{selectedCountry.name}</p>
                  <p className="text-sm text-gray-600">
                    {selectedCountry.currency.symbol} {selectedCountry.currency.name}
                  </p>
                </div>
              ) : (
                <p className="text-gray-500">{placeholder}</p>
              )}
            </div>
          </div>
          <ChevronDown className={`w-5 h-5 text-gray-400 transition-transform ${isOpen ? 'rotate-180' : ''}`} />
        </button>

        {isOpen && (
          <div className="absolute top-full left-0 right-0 z-50 mt-2 bg-white border border-gray-200 rounded-lg shadow-xl max-h-96 overflow-hidden min-w-full">
            {/* Search Input */}
            <div className="p-4 border-b border-gray-200">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search countries..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  autoFocus
                />
              </div>
            </div>

            {/* Countries List */}
            <div className="max-h-80 overflow-y-auto">
              {loading ? (
                <div className="p-4 text-center text-gray-500">
                  <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600 mx-auto mb-2"></div>
                  Loading countries...
                </div>
              ) : countries.length === 0 ? (
                <div className="p-4 text-center text-gray-500">
                  <div className="text-red-500 mb-2">‚ö†Ô∏è</div>
                  Failed to load countries. Please refresh the page.
                </div>
              ) : filteredCountries.length === 0 ? (
                <div className="p-4 text-center text-gray-500">
                  <div className="text-gray-400 mb-2">üîç</div>
                  No countries found for "{searchTerm}"
                </div>
              ) : (
                filteredCountries.map((country) => (
                  <button
                    key={country.code}
                    onClick={() => handleCountrySelect(country)}
                    className={`w-full flex items-center justify-between p-4 hover:bg-blue-50 transition-colors ${
                      selectedCountry?.code === country.code ? 'bg-blue-50' : ''
                    }`}
                  >
                    <div className="flex items-center">
                      <div className="w-8 h-6 bg-gradient-to-r from-blue-400 to-purple-500 rounded mr-3 flex items-center justify-center text-white text-xs font-bold">
                        {country.code}
                      </div>
                      <div className="text-left">
                        <p className="font-medium text-gray-900">{country.name}</p>
                        <p className="text-sm text-gray-600">
                          {country.currency.symbol} {country.currency.name}
                        </p>
                      </div>
                    </div>
                    {selectedCountry?.code === country.code && (
                      <Check className="w-5 h-5 text-blue-600" />
                    )}
                  </button>
                ))
              )}
            </div>
          </div>
        )}
      </GlassCard>
    </div>
  );
};

export default CountrySelector;
