import React from 'react';
import { Play, Clock, DollarSign } from 'lucide-react';

const VideosPage = () => {
  // Mock data - replace with actual API call
  const videos = [
    {
      id: 1,
      title: 'Introduction to React Hooks',
      description: 'Learn the basics of React Hooks and how to use them effectively.',
      thumbnail: 'https://via.placeholder.com/300x200',
      duration: 1200, // in seconds
      reward: 0.50,
      category: 'education'
    },
    {
      id: 2,
      title: 'JavaScript ES6 Features',
      description: 'Explore the new features introduced in ES6 JavaScript.',
      thumbnail: 'https://via.placeholder.com/300x200',
      duration: 900,
      reward: 0.40,
      category: 'education'
    },
    {
      id: 3,
      title: 'CSS Grid Layout Tutorial',
      description: 'Master CSS Grid Layout with practical examples.',
      thumbnail: 'https://via.placeholder.com/300x200',
      duration: 1500,
      reward: 0.60,
      category: 'education'
    }
  ];

  const formatDuration = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow p-6">
        <h1 className="text-2xl font-bold text-gray-900">Available Videos</h1>
        <p className="text-gray-600 mt-2">
          Watch videos and earn money. Select a video to start earning!
        </p>
      </div>

      {/* Videos Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {videos.map((video) => (
          <div key={video.id} className="bg-white rounded-lg shadow overflow-hidden hover:shadow-lg transition-shadow">
            <div className="relative">
              <img
                src={video.thumbnail}
                alt={video.title}
                className="w-full h-48 object-cover"
              />
              <div className="absolute inset-0 bg-black bg-opacity-0 hover:bg-opacity-20 transition-all duration-200 flex items-center justify-center">
                <button className="bg-white bg-opacity-90 rounded-full p-3 hover:bg-opacity-100 transition-all">
                  <Play className="w-6 h-6 text-gray-800" />
                </button>
              </div>
            </div>
            
            <div className="p-4">
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                {video.title}
              </h3>
              <p className="text-gray-600 text-sm mb-4">
                {video.description}
              </p>
              
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-4 text-sm text-gray-500">
                  <div className="flex items-center">
                    <Clock className="w-4 h-4 mr-1" />
                    {formatDuration(video.duration)}
                  </div>
                  <div className="flex items-center">
                    <DollarSign className="w-4 h-4 mr-1" />
                    ${video.reward.toFixed(2)}
                  </div>
                </div>
                
                <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors">
                  Watch & Earn
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default VideosPage;
